import React, { useState } from "react";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";

export default function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  //WORK IN PROGRESS
  const [filteredExpenses, setFilteredExpenses] = useState(props.items);

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses2 = filteredExpenses.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  // WORK IN PROGRESS
  const deleteExpenseHandler = (goalId) => {
    setFilteredExpenses((filteredExpenses) => {
      const updatedExpenses = filteredExpenses.filter(
        (items) => items.id !== goalId
      );
      return updatedExpenses;
    });
  };

  return (
    <Card className="expenses">
      <ExpenseFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      <ExpensesChart expenses={filteredExpenses2} />
      <ExpensesList
        items={filteredExpenses2}
        onDeleteItem={deleteExpenseHandler}
      />
    </Card>
  );
}
